import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";
export default function Home() {

  let { address } = useParams();
  let number = parseFloat(address.split('-')[0])
  let tld = address.split('-')[1] + ' ' + address.split('-')[2] 
  let n1 = '/' + (number+2).toString() + '-' + tld
  let n2 = '/' + (number-2).toString() + '-' + tld
  function isn1(){
    if (number + 2 <= 138) return true;
  }
  function isn2(){
    if (number - 2 > 0) return true;
  }

  return(
    <div>
   <h2>{address.replace('-',' ')}</h2>
   <div>
   Neighbours: <br />

   { isn1() && (
    <>
   <br />
            <Link to={n1}>{number+2} {tld}</Link>
            </>
       )}
   { isn2() && (
    <>
        <br />
            <Link to={n2}>{number-2} {tld}</Link>
            </>
        )}
        </div>
        </div>
   )
}
